import React, { useRef, useEffect, useState } from 'react';
import * as d3 from 'd3';
import './Graph.css';
import api from '../services/api.js';
import PlusIcon from './icons/PlusIcon';
import LinkIcon from './icons/LinkIcon';
import TrashIcon from './icons/TrashIcon';

const Graph = ({ nodes = [], edges = [], onNodeClick, onNodeDoubleClick, onCreateNew, onCreateEdge, onDeleteEdge, interactionMode = 'select', setInteractionMode, onBackgroundClick }) => {
  const svgRef = useRef();
  const linkingState = useRef({ sourceNode: null, tempLink: null, isLinking: false });
  const [selectedEdge, setSelectedEdge] = useState(null);
  const clickTimeout = useRef(null);

  console.log('Graph received:', {
    nodeCount: nodes.length,
    edgeCount: edges.length,
    mode: interactionMode
  });

  // Debug: Log the actual nodes and edges data
  console.log('Nodes data:', JSON.stringify(nodes, null, 2));
  console.log('Edges data:', JSON.stringify(edges, null, 2));

  useEffect(() => {
    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();

    if (!nodes || nodes.length === 0) {
      return;
    }

    const containerWidth = svgRef.current.clientWidth;
    const containerHeight = svgRef.current.clientHeight;

    svg
      .attr('viewBox', [0, 0, containerWidth, containerHeight])
      .attr('preserveAspectRatio', 'xMidYMid meet');

    svg.append('rect')
      .attr('class', 'background')
      .attr('width', '100%')
      .attr('height', '100%')
      .attr('fill', 'transparent')
      .on('click', (event) => {
        if (event.target.classList.contains('background')) {
          setSelectedEdge(null);
          if (onBackgroundClick) {
            onBackgroundClick();
          }
        }
      });

    // Add arrow marker definition for edges
    svg.append("defs").selectAll("marker")
      .data(["arrow"])
      .enter().append("marker")
      .attr("id", d => d)
      .attr("viewBox", "0 -5 10 10")
      .attr("refX", 20) // Increased from 15 to position arrow better
      .attr("refY", 0)
      .attr("markerWidth", 6)
      .attr("markerHeight", 6)
      .attr("orient", "auto")
      .append("path")
      .attr("d", "M0,-5L10,0L0,5")
      .attr("fill", "#6c757d");

    // Initialize node positions if not set
    nodes.forEach(node => {
      if (!node.position || (node.position.x === null && node.position.y === null)) {
        node.x = Math.random() * (containerWidth - 100) + 50;
        node.y = Math.random() * (containerHeight - 100) + 50;
        node.position = { x: node.x, y: node.y };
      } else {
        node.x = node.position.x;
        node.y = node.position.y;
      }
      // Don't fix positions by default to allow force layout to work
      node.fx = null;
      node.fy = null;
    });

    // Create a map of nodes by ID for easy lookup
    const nodeMap = {};
    nodes.forEach(node => {
      nodeMap[node._id] = node;
    });

    // Process edges to ensure they reference actual node objects
    const processedEdges = edges.map(edge => ({
      id: edge._id,
      source: nodeMap[edge.source] || edge.source,
      target: nodeMap[edge.target] || edge.target,
      type: edge.type,
      _id: edge._id
    }));

    const simulation = d3.forceSimulation(nodes)
      .force('link', d3.forceLink(processedEdges).id(d => d._id).distance(150))
      .alpha(0)
      .alphaDecay(1)
      .alphaTarget(0);

    // Fix node positions immediately
    nodes.forEach(node => {
      if (node.position) {
        node.fx = node.position.x;
        node.fy = node.position.y;
      } else {
        node.fx = Math.random() * (containerWidth - 100) + 50;
        node.fy = Math.random() * (containerHeight - 100) + 50;
        node.position = { x: node.fx, y: node.fy };
      }
    });

    // Create edge paths with arrows, appended directly to svg
    const linkElements = svg.append('g')
      .attr('class', 'links')
      .selectAll('path')
      .data(processedEdges)
      .enter().append('path')
      .attr('class', 'link')
      .attr('stroke', '#6c757d')
      .attr('stroke-width', 2)
      .attr('fill', 'none')
      .attr('marker-end', 'url(#arrow)')
      .on('click', handleEdgeClick);

    // Create node elements, appended directly to svg
    const nodeElements = svg.append('g')
      .attr('class', 'nodes')
      .selectAll('g')
      .data(nodes)
      .enter().append('g')
      .attr('class', 'node')
      .call(dragBehavior(simulation, containerWidth, containerHeight));

    nodeElements.append('circle')
      .attr('r', 12)
      .on('click', handleNodeClick);

    nodeElements.append('text')
      .text(d => d.title || d._id?.substring(0, 6))
      .attr('x', 16)
      .attr('y', 5)
      .attr('text-anchor', 'start')
      .on('click', handleNodeClick);

    function handleEdgeClick(event, d) {
      event.stopPropagation();
      if (interactionMode === 'select') {
        d3.selectAll('.link').classed('selected', false);
        d3.select(event.currentTarget).classed('selected', true);
        setSelectedEdge(d);
        if (onNodeClick) onNodeClick(null);
      }
    }

    svg.on('click', (event) => {
      if (event.target.classList.contains('background') || event.target === svgRef.current) {
        d3.selectAll('.link').classed('selected', false);
        setSelectedEdge(null);
        if (onBackgroundClick) {
          onBackgroundClick();
        }
      }
    });

    function ticked() {
      linkElements.attr('d', d => {
        const sourceX = Math.max(15, Math.min(containerWidth - 15, d.source.x));
        const sourceY = Math.max(15, Math.min(containerHeight - 15, d.source.y));
        const targetX = Math.max(15, Math.min(containerWidth - 15, d.target.x));
        const targetY = Math.max(15, Math.min(containerHeight - 15, d.target.y));
        
        return `M${sourceX},${sourceY}L${targetX},${targetY}`;
      });

      nodeElements.attr('transform', d => {
        const x = Math.max(15, Math.min(containerWidth - 15, d.x));
        const y = Math.max(15, Math.min(containerHeight - 15, d.y));
        return `translate(${x},${y})`;
      });
    }

    simulation.on('tick', ticked);
    
    return () => {
      simulation.stop();
      if (linkingState.current.tempLink) {
        linkingState.current.tempLink.remove();
      }
      linkingState.current = { sourceNode: null, tempLink: null, isLinking: false };
      if (clickTimeout.current) {
        clearTimeout(clickTimeout.current);
      }
    };
  }, [nodes, edges, onNodeClick, onNodeDoubleClick, onCreateEdge, interactionMode, onBackgroundClick]);

  // Handle keyboard delete for selected edges
  useEffect(() => {
    const handleKeyDown = (event) => {
      if ((event.key === 'Delete' || event.key === 'Backspace') && selectedEdge) {
        if (selectedEdge && selectedEdge._id && onDeleteEdge) {
          onDeleteEdge(selectedEdge._id);
          setSelectedEdge(null); // Deselect after deletion
        }
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [selectedEdge, onDeleteEdge]);

  function dragBehavior(simulation, containerWidth, containerHeight) {
    function dragstarted(event, d) {
      if (interactionMode === 'connect') {
        linkingState.current.isLinking = true;
        linkingState.current.sourceNode = d;
        event.sourceEvent.stopPropagation();
        linkingState.current.tempLink = svg.append('path')
          .attr('class', 'temp-link link')
          .attr('d', `M${d.x},${d.y}L${d.x},${d.y}`)
          .attr('stroke-dasharray', '5,5')
          .attr('stroke', '#6c757d')
          .attr('stroke-width', 2)
          .attr('fill', 'none')
          .attr('marker-end', 'url(#arrow)');
      } else {
        event.sourceEvent.stopPropagation();
        if (!event.active) simulation.alphaTarget(0.3).restart();
        d.fx = Math.max(15, Math.min(containerWidth - 15, d.x));
        d.fy = Math.max(15, Math.min(containerHeight - 15, d.y));
      }
    }

    function dragged(event, d) {
      if (interactionMode === 'connect' && linkingState.current.isLinking) {
        event.sourceEvent.stopPropagation();
        const targetX = Math.max(0, Math.min(containerWidth, event.x));
        const targetY = Math.max(0, Math.min(containerHeight, event.y));
        linkingState.current.tempLink
          .attr('d', `M${linkingState.current.sourceNode.x},${linkingState.current.sourceNode.y}L${targetX},${targetY}`);
      } else {
        event.sourceEvent.stopPropagation();
        d.fx = Math.max(15, Math.min(containerWidth - 15, event.x));
        d.fy = Math.max(15, Math.min(containerHeight - 15, event.y));
        d.position = { x: d.fx, y: d.fy };
      }
    }

    async function dragended(event, d) {
      if (interactionMode === 'connect' && linkingState.current.isLinking) {
        event.sourceEvent.stopPropagation();
        if (linkingState.current.tempLink) {
          linkingState.current.tempLink.remove();
          linkingState.current.tempLink = null;
        }

        const targetElement = event.sourceEvent.target;
        if (targetElement && (targetElement instanceof Element || targetElement instanceof SVGElement)) {
          let targetNodeElement = targetElement;

          while (targetNodeElement &&
                 (targetNodeElement instanceof Element || targetNodeElement instanceof SVGElement) &&
                 !d3.select(targetNodeElement).classed('node')) {
            if (!targetNodeElement.parentNode || !(targetNodeElement.parentNode instanceof Element || targetNodeElement.parentNode instanceof SVGElement) ) {
                targetNodeElement = null;
                break;
            }
            targetNodeElement = targetNodeElement.parentNode;
          }

          if (targetNodeElement &&
              (targetNodeElement instanceof Element || targetNodeElement instanceof SVGElement) &&
              d3.select(targetNodeElement).classed('node')) {
            const targetDatum = d3.select(targetNodeElement).datum();

            if (targetDatum && targetDatum._id && linkingState.current.sourceNode && targetDatum._id !== linkingState.current.sourceNode._id) {
              if (onCreateEdge) {
                onCreateEdge(linkingState.current.sourceNode._id, targetDatum._id);
              }
            }
          }
        }
        linkingState.current = { sourceNode: null, tempLink: null, isLinking: false };
      } else {
        if (!event.active) simulation.alphaTarget(0);
        d.fx = event.x;
        d.fy = event.y;
        d.position = { x: d.fx, y: d.fy };

        try {
          if (d._id) {
            await api.updateNodePosition(d._id, d.position);
          }
        } catch (err) {
          console.error('Failed to save node position:', err);
        }
      }
    }

    return d3.drag()
      .on('start', dragstarted)
      .on('drag', dragged)
      .on('end', dragended);
  }

  const handleNodeClick = (event, d) => {
    event.stopPropagation();
    setSelectedEdge(null);

    if (interactionMode === 'connect') {
      // Handle connection mode logic
      return;
    }

    if (clickTimeout.current) {
      // Double click detected
      clearTimeout(clickTimeout.current);
      clickTimeout.current = null;
      if (onNodeDoubleClick) {
        onNodeDoubleClick(d);
      }
    } else {
      // First click - wait for potential second click
      clickTimeout.current = setTimeout(() => {
        clickTimeout.current = null;
        if (onNodeClick) {
          onNodeClick(d);
        }
      }, 250); // 250ms delay to detect double click
    }
  };

  // Handle edge deletion
  const handleDeleteEdge = () => {
    if (selectedEdge && selectedEdge._id && onDeleteEdge) {
      onDeleteEdge(selectedEdge._id);
      setSelectedEdge(null);
    }
  };

  return (
    <div className="graph-wrapper" onClick={(e) => {
      if (e.target === e.currentTarget) {
        if (onBackgroundClick) {
          onBackgroundClick();
        }
      }
    }}>
      {nodes && nodes.length > 0 ? (
        <svg ref={svgRef} className="graph-svg" />
      ) : (
        <div className="graph-svg empty-graph-placeholder">
          <div className="empty-graph-message">
            <p>This canvas is empty. Start by creating a new node!</p>
            {/* The main "Create New Node" button is in graph-controls below */}
          </div>
        </div>
      )}
      <div className="graph-controls">
        {selectedEdge && (
          <button
            onClick={handleDeleteEdge}
            className="button danger"
          >
            <TrashIcon style={{ width: '1rem', height: '1rem', marginRight: '0.5rem' }} />
            Remove Edge
          </button>
        )}
        <div className="button-row">
          <button onClick={onCreateNew} className="button primary">
            <PlusIcon style={{ width: '1rem', height: '1rem', marginRight: '0.5rem' }} />
            Create New Node
          </button>
          <button
            onClick={() => setInteractionMode(prevMode => prevMode === 'connect' ? 'select' : 'connect')}
            className={`button secondary ${interactionMode === 'connect' ? 'active' : ''}`}
          >
            <LinkIcon style={{ width: '1rem', height: '1rem', marginRight: '0.5rem' }} />
            {interactionMode === 'connect' ? 'Stop Connecting' : 'Connect Nodes'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Graph;
