import React, { useState, useEffect } from 'react';
import Graph from './components/graph.js';
import NodeEditor from './components/NodeEditor.js';
import api from './services/api.js';
import './App.css';

function App() {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [selectedNode, setSelectedNode] = useState(null);
  const [isCreating, setIsCreating] = useState(false);
  const [interactionMode, setInteractionMode] = useState('select'); // 'select', 'connect', or 'reposition'

  useEffect(() => { fetchData(); }, []);

  const fetchData = async () => {
    try {
      const [nodesRes, edgesRes] = await Promise.all([
        api.getNodes(),
        api.getEdges()
      ]);
      setNodes([...nodesRes.data]);
      setEdges([...edgesRes.data]);
      
      console.log('Data refreshed:', {
        nodes: nodesRes.data.length,
        edges: edgesRes.data.length
      });

      // Debug: Log the actual data
      console.log('Nodes data from API:', JSON.stringify(nodesRes.data, null, 2));
      console.log('Edges data from API:', JSON.stringify(edgesRes.data, null, 2));
    } catch (err) {
      console.error('Refresh failed:', err);
    }
  };
  
  const handleDelete = async (id) => {
    try {
      await api.deleteNode(id);
      await fetchData(); // Refresh graph data
    } catch (err) {
      console.error(`Deletion failed: ${err.response?.data?.message || err.message}`);
    }
  };
  const handleCreateNew = () => {
    setSelectedNode(null);
    setIsCreating(true);
  };

  const handleCreateEdge = async (sourceId, targetId) => {
    try {
      console.log(`Creating edge from ${sourceId} to ${targetId}`);

      // Check if an edge already exists (optional, but good practice)
      const existingEdge = edges.find(
        edge => (edge.source === sourceId && edge.target === targetId) ||
                (edge.source === targetId && edge.target === sourceId)
      );
      if (existingEdge) {
        console.log('An edge already exists between these nodes.');
        return;
      }

      const response = await api.createEdge({ source: sourceId, target: targetId });
      console.log('Edge created:', response.data);
      await fetchData(); // Refresh graph data
    } catch (err) {
      console.error('Edge creation error:', err);
    }
  };
  
  const handleSave = async (nodeData) => {
    console.group('Saving Node Process');
    try {
      console.log('Final payload:', JSON.stringify(nodeData, null, 2));
      
      const response = nodeData._id
        ? await api.updateNode(nodeData._id, nodeData)
        : await api.createNode(nodeData);

      console.log('Save successful!', response.data);
      await fetchData();
    } catch (err) {
      console.error('Save failed:', {
        error: err,
        request: err.config,
        response: err.response?.data
      });
    } finally {
      console.groupEnd();
      setSelectedNode(null);
      setIsCreating(false);
    }
  };

  const handleDeleteEdge = async (edgeId) => {
    try {
      await api.deleteEdge(edgeId);
      await fetchData(); // Refresh graph data
    } catch (err) {
      console.error(`Edge deletion failed: ${err.response?.data?.message || err.message}`);
    }
  };

  return (
    <div className="app-container">
      <div className="graph-container">
        <Graph 
          nodes={nodes || []}       // Fallback empty array
          edges={edges || []}       // Fallback empty array
          onNodeClick={setSelectedNode}
          onCreateNew={handleCreateNew}
          onCreateEdge={handleCreateEdge}
          onDeleteEdge={handleDeleteEdge}
          interactionMode={interactionMode}
          // setInteractionMode will no longer be passed to Graph
        />
      </div>
      <div className="editor-container">
        {/* Mode toggle buttons moved inside the empty-state div below */}
        {(selectedNode || isCreating) ? (
          <NodeEditor
            node={selectedNode || { title: '', content: '', tags: [] }}
            onSave={handleSave}
            onDelete={selectedNode ? () => handleDelete(selectedNode._id) : null}
            onCancel={() => {
              setSelectedNode(null);
              setIsCreating(false);
            }}
          />
        ) : (
          <div className="empty-state">
            <p>Select a node or</p>
            <button onClick={handleCreateNew}>Create New Node</button>
            {/* Mode toggle buttons moved here, below "Create New Node" */}
            <div className="mode-toggle-buttons" style={{ marginTop: '10px' }}> {/* Added margin for spacing */}
              <button
                onClick={() => setInteractionMode(prevMode => prevMode === 'reposition' ? 'select' : 'reposition')}
                className={interactionMode === 'reposition' ? 'active' : ''} // Keep active class, remove mode-btn
              >
                Reposition Nodes
              </button>
              <button
                onClick={() => setInteractionMode(prevMode => prevMode === 'connect' ? 'select' : 'connect')}
                className={interactionMode === 'connect' ? 'active' : ''} // Keep active class, remove mode-btn
              >
                Connect Nodes
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
export default App;
