import React, { useRef, useEffect } from 'react';
import * as d3 from 'd3';
import './Graph.css';

const Graph = ({ nodes = [], edges = [], onNodeClick, onCreateNew }) => {
  console.log('Graph received:', { 
    nodeCount: nodes.length,
    edgeCount: edges.length 
  });
  const svgRef = useRef();

  useEffect(() => {
    if (!nodes || nodes.length === 0) return;
    
    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();
    const width = svgRef.current.clientWidth;
    const height = svgRef.current.clientHeight;
    const simulation = d3.forceSimulation(nodes)
      .force('charge', d3.forceManyBody().strength(-100))
      .force('center', d3.forceCenter(width/2, height/2));

    svg.selectAll('circle')
      .data(nodes)
      .enter()
      .append('circle')
      .attr('r', 10)
      .attr('fill', 'steelblue');

    simulation.on('tick', () => {
      svg.selectAll('circle')
        .attr('cx', d => d.x)
        .attr('cy', d => d.y);
    });

    return () => simulation.stop();
  }, [nodes,edges]);
  
  return (
    <div className="graph-wrapper">
      <svg ref={svgRef} width="600" height="600" className="graph-svg" />
      <button className="create-btn" onClick={onCreateNew}>
        + New Node
      </button>
    </div>
  );
};

// console.log(nodes); // Add this line for debugging

export default Graph; // This line is critical
