import React, { useRef, useEffect, useState } from 'react';
import * as d3 from 'd3';
import './Graph.css';
import api from '../services/api.js';

const Graph = ({ nodes = [], edges = [], onNodeClick, onCreateNew, onCreateEdge, onDeleteEdge, interactionMode = 'select', setInteractionMode }) => {
  const svgRef = useRef();
  const linkingState = useRef({ sourceNode: null, tempLink: null, isLinking: false });
  const [selectedEdge, setSelectedEdge] = useState(null);

  console.log('Graph received:', {
    nodeCount: nodes.length,
    edgeCount: edges.length,
    mode: interactionMode
  });

  // Debug: Log the actual nodes and edges data
  console.log('Nodes data:', JSON.stringify(nodes, null, 2));
  console.log('Edges data:', JSON.stringify(edges, null, 2));

  useEffect(() => {
    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();

    if (!nodes || nodes.length === 0) {
      return;
    }
    
    const width = svgRef.current.clientWidth;
    const height = svgRef.current.clientHeight;

    // Add arrow marker definition for edges
    svg.append("defs").selectAll("marker")
      .data(["arrow"])
      .enter().append("marker")
      .attr("id", d => d)
      .attr("viewBox", "0 -5 10 10")
      .attr("refX", 15) // Position the arrow near the target node
      .attr("refY", 0)
      .attr("markerWidth", 6)
      .attr("markerHeight", 6)
      .attr("orient", "auto")
      .append("path")
      .attr("d", "M0,-5L10,0L0,5")
      .attr("fill", "#6c757d");

    // Initialize node positions from stored values if available
    nodes.forEach(node => {
      if (node.position && node.position.x !== null && node.position.y !== null) {
        node.x = node.position.x;
        node.y = node.position.y;
        // Fix the position of nodes that have been positioned
        node.fx = node.position.x;
        node.fy = node.position.y;
      }
    });

    // Create a map of nodes by ID for easy lookup
    const nodeMap = {};
    nodes.forEach(node => {
      nodeMap[node._id] = node;
    });

    // Process edges to ensure they reference actual node objects
    const processedEdges = edges.map(edge => {
      // Create a new edge object with proper source and target references
      return {
        id: edge._id,
        source: nodeMap[edge.source] || edge.source,
        target: nodeMap[edge.target] || edge.target,
        type: edge.type,
        _id: edge._id // Keep the original ID for later reference
      };
    });

    // Debug: Log the processed edges
    console.log('Processed edges:', JSON.stringify(processedEdges, null, 2));

    const simulation = d3.forceSimulation(nodes)
      .force('link', d3.forceLink(processedEdges).id(d => d._id).distance(100))
      .force('charge', d3.forceManyBody().strength(-200))
      .force('center', d3.forceCenter(width / 2, height / 2))
      .alpha(0.1) // Reduce initial alpha to minimize movement
      .alphaDecay(0.05) // Faster decay to settle quicker
      .on('tick', ticked);

    // Create edge paths with arrows
    const linkElements = svg.append('g')
      .attr('class', 'links')
      .selectAll('path')
      .data(processedEdges)
      .enter().append('path')
      .attr('class', 'link')
      .attr('stroke', '#6c757d')
      .attr('stroke-width', 2)
      .attr('fill', 'none')
      .attr('marker-end', 'url(#arrow)')
      .on('click', handleEdgeClick);

    const nodeElements = svg.append('g')
      .attr('class', 'nodes')
      .selectAll('g')
      .data(nodes)
      .enter().append('g')
      .attr('class', 'node')
      .call(dragBehavior(simulation, interactionMode, svg, linkingState, onCreateEdge));

    nodeElements.append('circle')
      .attr('r', 10)
      .on('click', function(event, d) {
        event.stopPropagation();
        // Clear selected edge when clicking a node
        setSelectedEdge(null);
        if (onNodeClick) {
          onNodeClick(d);
        }
      });

    nodeElements.append('text')
      .text(d => d.title || d._id?.substring(0, 6))
      .attr('x', 15)
      .attr('y', 5)
      .on('click', function(event, d) {
        event.stopPropagation();
        // Clear selected edge when clicking a node's text
        setSelectedEdge(null);
        if (onNodeClick) {
          onNodeClick(d);
        }
      });

    function handleNodeClick(event, d) {
      event.stopPropagation();
      setSelectedEdge(null); // Clear selected edge when clicking a node
      if (onNodeClick) onNodeClick(d);
    }

    function handleEdgeClick(event, d) {
      event.stopPropagation();
      if (interactionMode === 'select') {
        // Highlight the selected edge
        d3.selectAll('.link').classed('selected', false);
        d3.select(event.currentTarget).classed('selected', true);
        setSelectedEdge(d);

        // Prevent node selection
        if (onNodeClick) onNodeClick(null);
      }
    }

    // Handle clicks on the background to deselect
    svg.on('click', () => {
      d3.selectAll('.link').classed('selected', false);
      setSelectedEdge(null);
    });

    function ticked() {
      // Update link paths for curved edges with arrows
      linkElements.attr('d', d => {
        const dx = d.target.x - d.source.x;
        const dy = d.target.y - d.source.y;
        const dr = Math.sqrt(dx * dx + dy * dy);

        // Direct path for arrows
        return `M${d.source.x},${d.source.y} L${d.target.x},${d.target.y}`;
      });
      nodeElements
        .attr('transform', d => `translate(${d.x},${d.y})`);
    }

    return () => {
      simulation.stop();
      if (linkingState.current.tempLink) linkingState.current.tempLink.remove();
      linkingState.current = { sourceNode: null, tempLink: null, isLinking: false };
    };
  }, [nodes, edges, onNodeClick, onCreateEdge, interactionMode]);

  // Drag handler factory - defined within Graph component scope, before return
  function dragBehavior(simulation, currentMode, svg, linkingStateRef, onCreateEdgeCallback) {
    function dragstarted(event, d) {
      if (currentMode === 'connect') {
        linkingStateRef.current.isLinking = true;
        linkingStateRef.current.sourceNode = d;
        linkingStateRef.current.tempLink = svg.append('path')
          .attr('class', 'temp-link link')
          .attr('d', `M${d.x},${d.y} L${d.x},${d.y}`)
          .attr('stroke-dasharray', '5,5')
          .attr('stroke', '#6c757d')
          .attr('stroke-width', 2)
          .attr('fill', 'none')
          .attr('marker-end', 'url(#arrow)');
        event.sourceEvent.stopPropagation();
      } else {
        // Always allow repositioning in non-connect mode
        if (!event.active) simulation.alphaTarget(0.3).restart();
        d.fx = d.x;
        d.fy = d.y;
      }
    }

    function dragged(event, d) {
      if (currentMode === 'connect' && linkingStateRef.current.isLinking) {
        linkingStateRef.current.tempLink
          .attr('d', `M${linkingStateRef.current.sourceNode.x},${linkingStateRef.current.sourceNode.y} L${event.x},${event.y}`);
      } else {
        // Allow free movement in all directions
        d.fx = event.x;
        d.fy = event.y;
        // Update the node's position property
        d.position = { x: event.x, y: event.y };
      }
    }

    async function dragended(event, d) {
      if (currentMode === 'connect' && linkingStateRef.current.isLinking) {
        if (linkingStateRef.current.tempLink) linkingStateRef.current.tempLink.remove();

        const targetElement = event.sourceEvent.target;
        let targetNodeElement = targetElement;
        while(targetNodeElement && !d3.select(targetNodeElement).classed('node')) {
          targetNodeElement = targetNodeElement.parentNode;
        }
        const targetDatum = d3.select(targetNodeElement).datum();

        if (targetDatum && targetDatum._id && linkingStateRef.current.sourceNode && targetDatum._id !== linkingStateRef.current.sourceNode._id) {
          if (onCreateEdgeCallback) {
            onCreateEdgeCallback(linkingStateRef.current.sourceNode._id, targetDatum._id);
          }
        }
        linkingStateRef.current = { sourceNode: null, tempLink: null, isLinking: false };
      } else {
        // Always allow repositioning in non-connect mode
        if (!event.active) simulation.alphaTarget(0);

        // Save the node position to the database
        try {
          if (d._id) {
            // Update both the fixed coordinates and position
            d.position = { x: d.fx, y: d.fy };
            await api.updateNodePosition(d._id, d.position);
            console.log(`Node position saved: ${d._id} at (${d.position.x}, ${d.position.y})`);
          }
        } catch (err) {
          console.error('Failed to save node position:', err);
        }
      }
    }

    return d3.drag()
        .on('start', dragstarted)
        .on('drag', dragged)
        .on('end', dragended);
  }

  // Handle edge deletion
  const handleDeleteEdge = () => {
    if (selectedEdge && selectedEdge._id && onDeleteEdge) {
      onDeleteEdge(selectedEdge._id);
      setSelectedEdge(null);
    }
  };

  return (
    <div className="graph-wrapper">
      <svg ref={svgRef} width="600" height="600" className="graph-svg" />
      <div className="graph-controls">
        {selectedEdge && (
          <button
            onClick={() => {
              if (selectedEdge && selectedEdge._id) {
                onDeleteEdge(selectedEdge._id);
                setSelectedEdge(null);
              }
            }}
            className="delete-edge-btn"
          >
            Remove Edge
          </button>
        )}
        <div className="button-row">
          <button onClick={onCreateNew} className="create-node-btn">
            Create New Node
          </button>
          <button
            onClick={() => setInteractionMode(prevMode => prevMode === 'connect' ? 'select' : 'connect')}
            className={`connect-btn ${interactionMode === 'connect' ? 'active' : ''}`}
          >
            {interactionMode === 'connect' ? 'Stop Connecting' : 'Connect Nodes'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Graph;
