import React, { useRef, useEffect } from 'react';
import * as d3 from 'd3';
import './Graph.css';

const Graph = ({ nodes = [], edges = [], onNodeClick, onCreateNew, onCreateEdge, interactionMode = 'select' }) => {
  const svgRef = useRef();
  const linkingState = useRef({ sourceNode: null, tempLink: null, isLinking: false });

  console.log('Graph received:', {
    nodeCount: nodes.length,
    edgeCount: edges.length,
    mode: interactionMode
  });

  useEffect(() => {
    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();

    if (!nodes || nodes.length === 0) {
      return;
    }
    
    const width = svgRef.current.clientWidth;
    const height = svgRef.current.clientHeight;

    const simulation = d3.forceSimulation(nodes)
      .force('link', d3.forceLink(edges).distance(100)) // Simplified: assumes source/target are node objects
      .force('charge', d3.forceManyBody().strength(-200))
      .force('center', d3.forceCenter(width / 2, height / 2))
      .on('tick', ticked);

    const linkElements = svg.append('g')
      .attr('class', 'links')
      .selectAll('line')
      .data(edges)
      .enter().append('line')
      .attr('class', 'link');

    const nodeElements = svg.append('g')
      .attr('class', 'nodes')
      .selectAll('g')
      .data(nodes)
      .enter().append('g')
      .attr('class', 'node')
      .call(dragBehavior(simulation, interactionMode, svg, linkingState, onCreateEdge));

    nodeElements.append('circle')
      .attr('r', 10)
      .on('click', handleNodeClick);
    
    nodeElements.append('text')
      .text(d => d.title || d.id.substring(0, 6))
      .attr('x', 15)
      .attr('y', 5);

    function handleNodeClick(event, d) {
      event.stopPropagation();
      if (interactionMode === 'select' || interactionMode === 'reposition') {
        if (onNodeClick) onNodeClick(d);
      }
      // In 'connect' mode, clicks are handled by drag start/end or ignored
    }

    function ticked() {
      linkElements
        .attr('x1', d => d.source.x)
        .attr('y1', d => d.source.y)
        .attr('x2', d => d.target.x)
        .attr('y2', d => d.target.y);

      nodeElements
        .attr('transform', d => `translate(${d.x},${d.y})`);
    }

    return () => {
      simulation.stop();
      if (linkingState.current.tempLink) linkingState.current.tempLink.remove();
      linkingState.current = { sourceNode: null, tempLink: null, isLinking: false };
    };
  }, [nodes, edges, onNodeClick, onCreateEdge, interactionMode]);

  // Drag handler factory - defined within Graph component scope, before return
  function dragBehavior(simulation, currentMode, svg, linkingStateRef, onCreateEdgeCallback) {
    function dragstarted(event, d) {
      if (currentMode === 'connect') {
        linkingStateRef.current.isLinking = true;
        linkingStateRef.current.sourceNode = d;
        linkingStateRef.current.tempLink = svg.append('line')
          .attr('class', 'temp-link link')
          .attr('x1', d.x)
          .attr('y1', d.y)
          .attr('x2', d.x)
          .attr('y2', d.y)
          .attr('stroke-dasharray', '5,5');
        event.sourceEvent.stopPropagation();
      } else if (currentMode === 'reposition') {
        if (!event.active) simulation.alphaTarget(0.3).restart();
        d.fx = d.x;
        d.fy = d.y;
      }
    }
    
    function dragged(event, d) {
      if (currentMode === 'connect' && linkingStateRef.current.isLinking) {
        linkingStateRef.current.tempLink
          .attr('x2', event.x)
          .attr('y2', event.y);
      } else if (currentMode === 'reposition') {
        d.fx = event.x;
        d.fy = event.y;
      }
    }
    
    function dragended(event, d) {
      if (currentMode === 'connect' && linkingStateRef.current.isLinking) {
        if (linkingStateRef.current.tempLink) linkingStateRef.current.tempLink.remove();
        
        const targetElement = event.sourceEvent.target;
        let targetNodeElement = targetElement;
        while(targetNodeElement && !d3.select(targetNodeElement).classed('node')) {
          targetNodeElement = targetNodeElement.parentNode;
        }
        const targetDatum = d3.select(targetNodeElement).datum();

        if (targetDatum && targetDatum.id && linkingStateRef.current.sourceNode && targetDatum.id !== linkingStateRef.current.sourceNode.id) {
          if (onCreateEdgeCallback) {
            onCreateEdgeCallback(linkingStateRef.current.sourceNode.id, targetDatum.id);
          }
        }
        linkingStateRef.current = { sourceNode: null, tempLink: null, isLinking: false };
      } else if (currentMode === 'reposition') {
        if (!event.active) simulation.alphaTarget(0);
      }
    }
    
    return d3.drag()
        .on('start', dragstarted)
        .on('drag', dragged)
        .on('end', dragended)
        .filter(() => currentMode === 'connect' || currentMode === 'reposition'); // Filter based on currentMode
  }

  return (
    <div className="graph-wrapper">
      <svg ref={svgRef} width="600" height="600" className="graph-svg" />
      {/* Controls including "+ New Node" button are removed from here as per feedback and moved to App.js */}
    </div>
  );
};

export default Graph;
