import React, { useState, useEffect } from 'react';
import './NodeEditor.css';

const NodeEditor = ({ node, onSave, onDelete, onCancel }) => {
  const [formData, setFormData] = useState({
    title: node?.title || '',
    content: node?.content || '',
    tags: node?.tags?.join(', ') || ''
  });
  const [isDeleting, setIsDeleting] = useState(false);

  // Update form data when node changes
  useEffect(() => {
    setFormData({
      title: node?.title || '',
      content: node?.content || '',
      tags: node?.tags?.join(', ') || ''
    });
  }, [node]);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted!');
    
    const tagsArray = formData.tags.split(',').map(tag => tag.trim());
    console.log('Data being sent:', {
      ...node,
      ...formData,
      tags: tagsArray
    });
    
    onSave({
      ...node,
      ...formData,
      tags: tagsArray
    });
  };

  const handleDelete = async () => {
    if (isDeleting) return;
    setIsDeleting(true);
    
    if (window.confirm('Permanently delete this node?')) {
      try {
        // Assuming onDelete is an async function that takes the node id
        // If onDelete doesn't take an id, and it's clear from context (e.g., current node), this is fine.
        // Otherwise, you might need to pass node._id: await onDelete(node._id);
        await onDelete();
      } finally {
        setIsDeleting(false);
        // onCancel might close the editor. Consider if this is desired on delete failure.
        if (onCancel) onCancel();
      }
    } else {
      setIsDeleting(false);
    }
  };
  
  return (
    <div className="editor-panel">
      <h2>{node?._id ? 'Edit Node' : 'Create Node'}</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Title</label>
          <input
            type="text"
            value={formData.title}
            onChange={(e) => setFormData({...formData, title: e.target.value})}
            required
          />
        </div>
        
        <div className="form-group">
          <label>Content</label>
          <textarea
            value={formData.content}
            onChange={(e) => setFormData({...formData, content: e.target.value})}
          />
        </div>
        
        <div className="form-group">
          <label>Tags (comma separated)</label>
          <input
            type="text"
            value={formData.tags}
            onChange={(e) => setFormData({...formData, tags: e.target.value})}
          />
        </div>
        
        <div className="button-group">
          <button type="submit" className="save-btn">
            Save
          </button>
          {onCancel && (
            <button type="button" className="cancel-btn" onClick={onCancel}>
              Cancel
            </button>
          )}
          {onDelete && node?._id && ( // Ensure node exists and has an ID for deletion
            <button
              type="button"
              className="delete-btn"
              onClick={handleDelete}
              disabled={isDeleting}
            >
              {isDeleting ? 'Deleting...' : 'Delete'}
            </button>
          )}
        </div>
      </form>
    </div>
  );
};

export default NodeEditor;
