import React, { useState, useEffect } from 'react';
import Graph from './components/graph.js';
import NodeEditor from './components/NodeEditor.js';
import FileList from './components/FileList.js';
import api from './services/api.js';
import './App.css';

function App() {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [selectedNode, setSelectedNode] = useState(null);
  const [isCreating, setIsCreating] = useState(false);
  const [interactionMode, setInteractionMode] = useState('select');
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    if (selectedFile) {
      loadFileData();
    }
  }, [selectedFile]);

  const loadFileData = async () => {
    try {
      console.log('Loading file data for:', selectedFile._id);
      const response = await api.getFile(selectedFile._id);
      console.log('File data received:', response.data);
      
      if (!response.data.nodes) {
        console.warn('No nodes array in file data');
        setNodes([]);
        setEdges([]);
        return;
      }

      // Ensure all nodes have positions
      const nodesWithPositions = response.data.nodes.map(node => {
        if (!node.position || (node.position.x === null && node.position.y === null)) {
          return {
            ...node,
            position: {
              x: Math.random() * 600 + 100, // Random position between 100 and 700
              y: Math.random() * 400 + 100  // Random position between 100 and 500
            }
          };
        }
        return node;
      });

      console.log('Nodes with positions:', nodesWithPositions);
      setNodes(nodesWithPositions);

      // Load edges related to these nodes
      const edgesResponse = await api.getEdges();
      const relevantEdges = edgesResponse.data.filter(edge => 
        response.data.nodes.some(node => node._id === edge.source) &&
        response.data.nodes.some(node => node._id === edge.target)
      );
      console.log('Relevant edges:', relevantEdges);
      setEdges(relevantEdges);
    } catch (err) {
      console.error('Error loading file data:', err);
    }
  };

  const handleFileSelect = (file) => {
    setSelectedFile(file);
  };

  const handleCreateNode = async () => {
    setSelectedNode(null);
    setIsCreating(true);
  };

  const handleNodeClick = (node) => {
    if (interactionMode !== 'connect') {
      setSelectedNode(node);
      setIsCreating(false);
    }
  };

  const handleSave = async (nodeData) => {
    try {
      console.group('Saving Node Process');
      console.log('Node data before save:', nodeData);
      
      // If this is a new node (no _id), set its initial position
      if (!nodeData._id) {
        let avgX = 300, avgY = 300;
        if (nodes.length > 0) {
          avgX = nodes.reduce((sum, node) => sum + (node.position?.x || 300), 0) / nodes.length;
          avgY = nodes.reduce((sum, node) => sum + (node.position?.y || 300), 0) / nodes.length;
        }

        const offset = 50;
        nodeData.position = {
          x: avgX + (Math.random() - 0.5) * offset,
          y: avgY + (Math.random() - 0.5) * offset
        };
      }
      
      const response = nodeData._id
        ? await api.updateNode(nodeData._id, nodeData)
        : await api.createNode(nodeData);

      console.log('Save response:', response.data);

      if (!nodeData._id && selectedFile) {
        // If it's a new node, add it to the current file
        const addToFileResponse = await api.addNodeToFile(selectedFile._id, response.data._id);
        console.log('Added to file response:', addToFileResponse.data);
      }

      await loadFileData(); // Reload the file data to get updated nodes
    } catch (err) {
      console.error('Save failed:', {
        error: err,
        request: err.config,
        response: err.response?.data
      });
    } finally {
      console.groupEnd();
      setSelectedNode(null);
      setIsCreating(false);
    }
  };

  const handleDelete = async (id) => {
    try {
      if (selectedFile) {
        // First remove the node from the file
        await api.removeNodeFromFile(selectedFile._id, id);
      }
      await api.deleteNode(id);
      await loadFileData();
    } catch (err) {
      console.error(`Deletion failed: ${err.response?.data?.message || err.message}`);
    }
  };

  const handleCreateEdge = async (sourceId, targetId) => {
    try {
      console.log(`Creating edge from ${sourceId} to ${targetId}`);

      const existingEdge = edges.find(
        edge => (edge.source === sourceId && edge.target === targetId) ||
                (edge.source === targetId && edge.target === sourceId)
      );
      if (existingEdge) {
        console.log('An edge already exists between these nodes.');
        return;
      }

      const response = await api.createEdge({ source: sourceId, target: targetId });
      console.log('Edge created:', response.data);
      await loadFileData();
      setInteractionMode('select');
    } catch (err) {
      console.error('Edge creation error:', err);
    }
  };

  const handleDeleteEdge = async (edgeId) => {
    try {
      await api.deleteEdge(edgeId);
      await loadFileData();
    } catch (err) {
      console.error(`Edge deletion failed: ${err.response?.data?.message || err.message}`);
    }
  };

  if (!selectedFile) {
    return <FileList onFileSelect={handleFileSelect} />;
  }

  return (
    <div className="app-container">
      <div className="header">
        <button onClick={() => setSelectedFile(null)} className="back-btn">
          Back to Files
        </button>
        <h2>{selectedFile.name}</h2>
      </div>
      <div className="main-content">
        <div className="graph-container">
          <Graph 
            nodes={nodes}
            edges={edges}
            onNodeClick={handleNodeClick}
            onCreateNew={handleCreateNode}
            onCreateEdge={handleCreateEdge}
            onDeleteEdge={handleDeleteEdge}
            interactionMode={interactionMode}
            setInteractionMode={setInteractionMode}
          />
        </div>
        <div className="editor-container">
          {(selectedNode || isCreating) ? (
            <NodeEditor
              node={selectedNode || { title: '', content: '', tags: [] }}
              onSave={handleSave}
              onDelete={selectedNode ? () => handleDelete(selectedNode._id) : null}
              onCancel={() => {
                setSelectedNode(null);
                setIsCreating(false);
              }}
            />
          ) : (
            <div className="empty-state">
              <p>Select a node to edit or create a new one</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
