import React, { useState, useEffect } from 'react';
import api from '../services/api';
import './FileList.css';

const FileList = ({ onFileSelect }) => {
  const [files, setFiles] = useState([]);
  const [newFileName, setNewFileName] = useState('');

  useEffect(() => {
    loadFiles();
  }, []);

  const loadFiles = async () => {
    try {
      const response = await api.getFiles();
      setFiles(response.data);
    } catch (err) {
      console.error('Error loading files:', err);
    }
  };

  const handleCreateFile = async (e) => {
    e.preventDefault();
    if (!newFileName.trim()) return;

    try {
      await api.createFile(newFileName);
      setNewFileName('');
      await loadFiles();
    } catch (err) {
      console.error('Error creating file:', err);
    }
  };

  const handleDoubleClick = (file) => {
    onFileSelect(file);
  };

  return (
    <div className="file-list-container">
      <h2>Files</h2>
      <form onSubmit={handleCreateFile} className="file-create-form">
        <input
          type="text"
          value={newFileName}
          onChange={(e) => setNewFileName(e.target.value)}
          placeholder="Enter file name"
          className="file-input"
        />
        <button type="submit" className="create-file-btn">Create File</button>
      </form>
      <div className="file-list">
        {files.map(file => (
          <div
            key={file._id}
            className="file-item"
            onDoubleClick={() => handleDoubleClick(file)}
          >
            <span className="file-number">#{file.srNo}</span>
            <span className="file-name">{file.name}</span>
            <span className="file-nodes-count">{file.nodes?.length || 0} nodes</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FileList;
