import React, { useState, useEffect, useMemo } from 'react';
import api from '../services/api';
import './FileList.css';
import PlusIcon from './icons/PlusIcon';

const FileList = ({ onFileSelect }) => {
  const [files, setFiles] = useState([]);
  const [newFileName, setNewFileName] = useState('');
  const [isCreating, setIsCreating] = useState(false);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [createError, setCreateError] = useState('');

  useEffect(() => {
    loadFiles();
  }, []);

  const loadFiles = async () => {
    try {
      const response = await api.getFiles();
      setFiles(response.data);
    } catch (err) {
      console.error('Error loading files:', err);
      setError('Failed to load files. Please try again.');
    }
  };

  const handleCreateFile = async (e) => {
    e.preventDefault();
    const trimmedName = newFileName.trim();
    
    if (!trimmedName) {
      setCreateError('Please enter a file name for the new file.');
      return;
    }

    if (files.some(file => file.name.toLowerCase() === trimmedName.toLowerCase())) {
      setCreateError('A file with this name already exists.');
      return;
    }

    setCreateError('');
    setError('');
    setIsCreating(true);

    try {
      const response = await api.createFile(trimmedName);
      setNewFileName('');
      await loadFiles();
      onFileSelect(response.data);
    } catch (err) {
      console.error('Error creating file:', err);
      setCreateError('Failed to create file. Please try again.');
    } finally {
      setIsCreating(false);
    }
  };

  const handleDoubleClick = (file) => {
    onFileSelect(file);
  };

  const filteredFiles = useMemo(() => {
    if (!searchTerm) {
      return files;
    }
    return files.filter(file => 
      file.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
  }, [files, searchTerm]);

  return (
    <div className="file-list-container">
      <h2>Files</h2>

      <div className="file-search-form">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search files by name..."
          className="file-input search-input"
        />
      </div>
      
      <form onSubmit={handleCreateFile} className="file-create-form-separated">
        <input
          type="text"
          value={newFileName}
          onChange={(e) => {
            setNewFileName(e.target.value);
            setError(''); // Clear error when user types
          }}
          placeholder="Enter file name"
          className="file-input"
          disabled={isCreating}
        />
        <button 
          type="submit" 
          className="button primary"
          disabled={isCreating}
        >
          <PlusIcon style={{ width: '1rem', height: '1rem', marginRight: '0.5rem' }} />
          {isCreating ? 'Creating...' : 'Create File'}
        </button>
      </form>
      {error && <div className="error-message">{error}</div>}
      <div className="file-list">
        {files.map(file => (
          <div
            key={file._id}
            className="file-item"
            onDoubleClick={() => handleDoubleClick(file)}
          >
            <span className="file-number">#{file.srNo}</span>
            <span className="file-name">{file.name}</span>
            <span className="file-nodes-count">{file.nodes?.length || 0} nodes</span>
          </div>
        ))}
        {files.length === 0 && (
          <div className="empty-files">
            No files yet. Create your first file!
          </div>
        )}
      </div>
    </div>
  );
};

export default FileList;
