import React, { useRef, useEffect, useState, useCallback } from 'react';
import * as d3 from 'd3';
import './Graph.css';
import api from '../services/api.js';
import PlusIcon from './icons/PlusIcon';
import LinkIcon from './icons/LinkIcon';
import TrashIcon from './icons/TrashIcon';

const Graph = ({ nodes = [], edges = [], onNodeClick, onNodeDoubleClick, onCreateNew, onCreateEdge, onDeleteEdge, interactionMode = 'select', setInteractionMode, onBackgroundClick, isAnyPanelVisible /*, newlyCreatedEdgeId, onEdgeAnimationComplete, appearingNodeId, onNodeAppearAnimationComplete */ }) => {
  console.log("CURRENT INTERACTION MODE:", interactionMode);
  const svgRef = useRef(null);
  const [isSvgMounted, setIsSvgMounted] = useState(false);
  const simulationRef = useRef(); // Ref to store the D3 simulation instance
  const linkingState = useRef({ sourceNode: null, tempLink: null, isLinking: false });
  const [selectedEdge, setSelectedEdge] = useState(null);
  const clickTimeout = useRef(null);
  const graphDimensionsRef = useRef({ width: 800, height: 600 }); // For App.js, needs live dimensions
  const stableViewBoxDimensionsRef = useRef({ width: 0, height: 0 }); // For D3 viewBox, needs initial stable dimensions

  // console.log('Graph received:', {
  //   nodeCount: nodes.length,
  //   edgeCount: edges.length,
  //   mode: interactionMode
  // });

  // Debug: Log the actual nodes and edges data
  // console.log('Nodes data:', JSON.stringify(nodes, null, 2));
  // console.log('Edges data:', JSON.stringify(edges, null, 2));

  console.log("Current onCreateEdge function:", onCreateEdge);

  const handleMouseMoveLinking = useCallback((event) => {
  if (!linkingState.current.isLinking || !linkingState.current.sourceNode || !linkingState.current.tempLink) {
    return;
  }

  if (!svgRef.current) return;
  
  const [x, y] = d3.pointer(event, svgRef.current);
  
  // Update the temp link to follow the mouse
  linkingState.current.tempLink
    .attr('d', `M${linkingState.current.sourceNode.x},${linkingState.current.sourceNode.y}L${x},${y}`);
    
}, [svgRef, linkingState]);


const handleMouseUpLinking = useCallback((event) => {
  if (linkingState.current.isLinking) {
    if (!svgRef.current) return;
    
    const clickedDomElement = event.target;
    const closestNodeGroup = clickedDomElement.closest('.node');
    const isNode = !!closestNodeGroup;

    if (!isNode) {
      console.log('Clicked on empty space - cancelling connection');
      
      // Clean up
      linkingState.current.tempLink?.remove();
      
      // Reset visual feedback
      d3.selectAll('.node circle')
        .attr('stroke', '#1B5E20')
        .attr('stroke-width', 1.5);
      
      linkingState.current.isLinking = false;
      linkingState.current.sourceNode = null;
      linkingState.current.tempLink = null;
      return;
    }

    // Get the target node ID
    const targetNodeElement = d3.select(closestNodeGroup);
    if (!targetNodeElement || !targetNodeElement.node()) {
      console.log('Failed to find target node element');
      linkingState.current.tempLink?.remove();
      linkingState.current.isLinking = false;
      linkingState.current.sourceNode = null;
      linkingState.current.tempLink = null;
      return;
    }

    const targetNode = targetNodeElement.datum();
    if (!targetNode || !targetNode._id) {
      console.log('Failed to get valid target node data');
      linkingState.current.tempLink?.remove();
      linkingState.current.isLinking = false;
      linkingState.current.sourceNode = null;
      linkingState.current.tempLink = null;
      return;
    }

    // Complete the connection
    const sourceNode = linkingState.current.sourceNode;
    if (sourceNode._id === targetNode._id) {
      console.log('Cannot connect node to itself');
      linkingState.current.tempLink?.remove();
      linkingState.current.isLinking = false;
      linkingState.current.sourceNode = null;
      linkingState.current.tempLink = null;
      return;
    }

    // Create the edge
    const edgeData = {
      source: sourceNode._id,
      target: targetNode._id,
      type: 'related',
      weight: 1
    };

    // Call the onCreateEdge callback
    if (onCreateEdge) {
      onCreateEdge(edgeData);
    }

    // Clean up
    linkingState.current.tempLink?.remove();
    linkingState.current.isLinking = false;
    linkingState.current.sourceNode = null;
    linkingState.current.tempLink = null;

    // Reset visual feedback
    d3.selectAll('.node circle')
      .attr('stroke', '#1B5E20')
      .attr('stroke-width', 1.5);
  }
}, [svgRef, linkingState, onCreateEdge]);
 // onCreateEdge might be relevant if we decide to complete link here

  useEffect(() => {
    const svgInstance = d3.select(svgRef.current);

    if (interactionMode === 'connect') {
      const onMouseMoveLinking = handleMouseMoveLinking;
      const onMouseUpLinking = handleMouseUpLinking;

      svgInstance.on('mousemove.linking', onMouseMoveLinking);
      svgInstance.on('mouseup.linking', onMouseUpLinking);
      // console.log('Global linking listeners ADDED to SVG for connect mode.');

      return () => {
        svgInstance.on('mousemove.linking', null);
        svgInstance.on('mouseup.linking', null);
        // console.log('Global linking listeners REMOVED from SVG.');
      };
    } else {
      svgInstance.on('mousemove.linking', null);
      svgInstance.on('mouseup.linking', null);

      if (linkingState.current.isLinking) {
        // console.log('Exited connect mode with active link: Forcibly cleaning up.');
        linkingState.current.tempLink?.remove();
        linkingState.current.isLinking = false;
        linkingState.current.sourceNode = null;
        linkingState.current.tempLink = null;
      }
      return () => { };
    }
  }, [interactionMode, svgRef, handleMouseMoveLinking, handleMouseUpLinking]);

  // Main effect for D3 rendering and updates
  useEffect(() => {
    // console.log('[Graph Effect - Main D3] Running. Nodes count:', nodes.length, 'Edges count:', edges.length);
    // console.log('[Graph Effect - Main D3] Nodes prop:', JSON.parse(JSON.stringify(nodes)));
    // console.log('[Graph Effect - Main D3] Edges prop:', JSON.parse(JSON.stringify(edges)));

    const currentSvg = svgRef.current;
    if (!currentSvg) {
      // console.log('[Graph Effect - Main D3] SVG ref not available yet. Aborting D3 setup.');
      return; // SVG element isn't rendered yet (e.g. nodes are empty)
    }

    // Initialize simulation IF it doesn't exist yet AND currentSvg is available
    if (!simulationRef.current) {
      // console.log('[Graph Effect - Main D3] Initializing simulation.');
      const initialWidth = graphDimensionsRef.current.width;
      const initialHeight = graphDimensionsRef.current.height;
      simulationRef.current = d3.forceSimulation()
        .force('link', d3.forceLink([]).id(d => d._id).distance(150))
        .alpha(0).alphaDecay(1).alphaTarget(0);
      // console.log('[Graph Effect - Main D3] Simulation initialized:', simulationRef.current);
    }

    const svg = d3.select(currentSvg); // Use currentSvg directly as it's confirmed not null
    const simulation = simulationRef.current;

    // This check should ideally not be hit if the above init logic works, but keep as safeguard
    if (!simulation) {
      // console.error('[Graph Effect - Main D3] Simulation ref is STILL not set! This is unexpected. Aborting.');
      return;
    }
    // console.log('[Graph Effect - Main D3] Simulation instance retrieved:', simulation);

    svg.on('click.diagnostic', (event) => {
      // console.log('[SVG DIAGNOSTIC CLICK] SVG itself was clicked. Target:', event.target);
    });

    // 1. Manage the background rectangle: append if not exists, ensure attributes.
    let backgroundRect = svg.select('rect.background');
    if (backgroundRect.empty()) {
      backgroundRect = svg.append('rect')
        .attr('class', 'background');
    }
    backgroundRect // Ensure attributes are set on every run if they could change (e.g. dimensions)
      .attr('width', '100%')
      .attr('height', '100%')
      .attr('fill', 'transparent')
      .on('click', (event) => {
        // console.log('[Background Rect Clicked Directly] Target:', event.target);
        if (event.target.classList.contains('background')) {
          d3.selectAll('.link').classed('selected', false);
          setSelectedEdge(null);
          if (onBackgroundClick) {
            onBackgroundClick();
          }
        }
      });

    // 2. Ensure dedicated groups for links and nodes exist (append if not).
    let linkGroup = svg.select('g.links-group');
    if (linkGroup.empty()) {
      linkGroup = svg.append('g').attr('class', 'links-group');
    }
    let nodeGroup = svg.select('g.nodes-group');
    if (nodeGroup.empty()) {
      nodeGroup = svg.append('g').attr('class', 'nodes-group');
    }

    // 3. Manage defs for markers: append if not exists.
    let defs = svg.select('defs');
    if (defs.empty()) {
      defs = svg.append('defs');
    }
    // Check if marker already exists before appending
    if (defs.select('#arrow').empty()) {
      defs.append("marker")
        .attr("id", "arrow")
        .attr("viewBox", "0 -5 10 10")
        .attr("refX", 20)
        .attr("refY", 0)
        .attr("markerWidth", 6)
        .attr("markerHeight", 6)
        .attr("orient", "auto")
        .append("path")
        .attr("d", "M0,-5L10,0L0,5")
        .attr("fill", "#A8C3A4");
    }

    // Determine live dimensions for clamping and for App.js new node positioning
    const liveContainerWidth = currentSvg.clientWidth;
    const liveContainerHeight = currentSvg.clientHeight;
    graphDimensionsRef.current = { width: liveContainerWidth, height: liveContainerHeight }; // Update for App.js

    // Set stable viewBox dimensions only once when valid live dimensions are first available
    if (stableViewBoxDimensionsRef.current.width === 0 && liveContainerWidth > 0) {
      stableViewBoxDimensionsRef.current.width = liveContainerWidth;
      // console.log(`[Graph Dimensions] Stable viewBoxWidth SET to: ${liveContainerWidth}`);
    }
    if (stableViewBoxDimensionsRef.current.height === 0 && liveContainerHeight > 0) {
      stableViewBoxDimensionsRef.current.height = liveContainerHeight;
      // console.log(`[Graph Dimensions] Stable viewBoxHeight SET to: ${liveContainerHeight}`);
    }

    // Use stable dimensions for the viewBox if available, otherwise fallback to live (should only be for very first paint)
    const viewBoxWidth = stableViewBoxDimensionsRef.current.width > 0 ? stableViewBoxDimensionsRef.current.width : liveContainerWidth;
    const viewBoxHeight = stableViewBoxDimensionsRef.current.height > 0 ? stableViewBoxDimensionsRef.current.height : liveContainerHeight;

    // console.log(`[Graph Dimensions] Using for viewBox: ${viewBoxWidth}x${viewBoxHeight}. Live: ${liveContainerWidth}x${liveContainerHeight}`);

    svg
      .attr('viewBox', [0, 0, viewBoxWidth, viewBoxHeight])
      .attr('preserveAspectRatio', 'xMidYMid meet');

    // Update node positions (fx, fy) before passing to simulation
    nodes.forEach(node => {
      const hasValidPositionProp = node.position && typeof node.position.x === 'number' && typeof node.position.y === 'number';

      if (hasValidPositionProp) {
        node.x = node.position.x;
        node.y = node.position.y;
        node.fx = node.position.x;
        node.fy = node.position.y;
      } else {
        // Position from prop is invalid or missing. Node should be allowed to be placed by simulation.
        // Or, if it's a new node without prior x/y, give it a random start.
        node.x = typeof node.x === 'number' ? node.x : (Math.random() * (liveContainerWidth - 100) + 50);
        node.y = typeof node.y === 'number' ? node.y : (Math.random() * (liveContainerHeight - 100) + 50);
        node.fx = null; // Explicitly nullify fx/fy if position from prop is not definitive.
        node.fy = null;

        // For consistency or if other parts of the code expect node.position to exist:
        // Update node.position to reflect the initial x,y that will be used by the simulation (if fx/fy are null).
        // This ensures that if this node object is reused, its .position reflects its current state.
        node.position = { x: node.x, y: node.y };
      }
    });
    // console.log('[D3 Data] Nodes after position init:', JSON.parse(JSON.stringify(nodes))); // Deep copy for logging

    // Validate and process nodes and edges
    const nodeMap = {};
    nodes.forEach(node => {
      if (node && node._id) {
        nodeMap[node._id] = node;
      }
    });

    // Filter out invalid edges
    const validEdges = edges.filter(edge => 
      edge && 
      edge.source && 
      edge.target && 
      nodeMap[edge.source] && 
      nodeMap[edge.target]
    );

    const processedEdges = validEdges.map(edge => ({
      id: edge._id,
      source: nodeMap[edge.source],
      target: nodeMap[edge.target],
      type: edge.type || 'related',
      _id: edge._id
    }));

    // Log edge processing for debugging
    console.log('Processed edges count:', processedEdges.length);
    console.log('Invalid edges count:', edges.length - validEdges.length);
    // console.log('[D3 Data] Processed Edges:', JSON.parse(JSON.stringify(processedEdges)));

    simulation
      .nodes(nodes)
      .on('tick', ticked);
    simulation.force('link').links(processedEdges);

    // Handle link rendering with null checks
    const linkElements = linkGroup.selectAll('path.link')
      .data(processedEdges, d => d.id)
      .join('path')
      .attr('class', 'link')
      .attr('data-id', d => d?.id || '')
      .attr('stroke', '#A8C3A4')
      .attr('stroke-width', 2)
      .attr('fill', 'none')
      .attr('marker-end', 'url(#arrow)');

    // Add error handling for click events
    linkElements.on('click', (event, d) => {
      try {
        handleEdgeClick(event, d);
      } catch (err) {
        console.error('Error handling edge click:', err);
      }
    });
    linkElements.on('click', handleEdgeClick);
    // console.log('[D3 Elements] linkElements count:', linkElements.size());

    const nodeElements = nodeGroup.selectAll('g.node')
  .data(nodes, d => d._id)
  .join(
    enter => {
      const g = enter.append('g')
        .attr('class', 'node')
        .attr('data-id', d => d._id);
      
      // Only apply drag behavior in select mode
      if (interactionMode === 'select') {
        g.call(dragBehavior(simulation, svg.node(), liveContainerWidth, liveContainerHeight));
      }

      g.append('circle')
        .attr('r', 12)
        .attr('fill', '#2E7D32')
        .attr('stroke', '#1B5E20')
        .attr('stroke-width', 1.5)
        .style('pointer-events', 'all');

      g.append('text')
        .text(d => d.title || d._id?.substring(0, 6))
        .attr('x', 16).attr('y', 5).attr('fill', '#FAF3E0').attr('text-anchor', 'start');

      // Make sure click events are attached
      g.on('click', handleNodeClick);
      console.log('🟢 Attached click handler to node:', g.datum()?._id);

      return g;
    },
    update => {
      // Re-apply or remove drag behavior based on current mode
      if (interactionMode === 'select') {
        update.call(dragBehavior(simulation, svg.node(), liveContainerWidth, liveContainerHeight));
      } else {
        // Remove drag behavior
        update.on('.drag', null);
      }
      
      // Make sure click events are still attached
      update.on('click', handleNodeClick);
      
      return update;
    },
    exit => exit.remove()
  );



    // console.log('[D3 Elements] nodeElements count:', nodeElements.size());

    const circles = nodeElements.select('circle');
    circles.on('click', handleNodeClick).style('pointer-events', 'all');

    function handleEdgeClick(event, d) {
      console.log('[Edge Click] Attempted on edge:', d.id, 'Mode:', interactionMode);
      event.stopPropagation();
      if (interactionMode === 'select') {
        d3.selectAll('.link').classed('selected', false);
        d3.select(event.currentTarget).classed('selected', true);
        setSelectedEdge(d);
        if (onNodeClick) { // This onNodeClick(null) is to deselect any selected node
          console.log("handleEdgeClick: Calling onNodeClick(null) to deselect node.");
          onNodeClick(null); // Deselects any selected node in App.js
        } else {
          console.log("handleEdgeClick: onNodeClick prop is undefined (cannot deselect node).");
        }
      } else {
        console.log("handleEdgeClick: Not in 'select' mode. Current mode:", interactionMode, "No action taken.");
      }
    }

    function ticked() {
      // console.log('[Ticked] Simulation tick occurred.');
      // if (nodes.length > 0 && nodes[0]) {
      //     console.log(`[Ticked] Sample node 0: x=${nodes[0].x}, y=${nodes[0].y}, fx=${nodes[0].fx}, fy=${nodes[0].fy}`);
      // }
      // if (linkElements.size() > 0 && processedEdges.length > 0 && processedEdges[0]) {
      //   const firstEdge = processedEdges[0];
      //   if (firstEdge && firstEdge.source && typeof firstEdge.source.x === 'number' && firstEdge.target && typeof firstEdge.target.x === 'number') {
      //        console.log(`[Ticked] Sample link 0 source: x=${firstEdge.source.x}, y=${firstEdge.source.y}. Target: x=${firstEdge.target.x}, y=${firstEdge.target.y}`);
      //   }
      // }

      linkElements.attr('d', d => {
        const sourceXPos = d.source.fx != null ? d.source.fx : d.source.x;
        const sourceYPos = d.source.fy != null ? d.source.fy : d.source.y;
        const targetXPos = d.target.fx != null ? d.target.fx : d.target.x;
        const targetYPos = d.target.fy != null ? d.target.fy : d.target.y;

        const sourceX = typeof sourceXPos === 'number' ? Math.max(15, Math.min(liveContainerWidth - 15, sourceXPos)) : 0;
        const sourceY = typeof sourceYPos === 'number' ? Math.max(15, Math.min(liveContainerHeight - 15, sourceYPos)) : 0;
        const targetX = typeof targetXPos === 'number' ? Math.max(15, Math.min(liveContainerWidth - 15, targetXPos)) : 0;
        const targetY = typeof targetYPos === 'number' ? Math.max(15, Math.min(liveContainerHeight - 15, targetYPos)) : 0;
        return `M${sourceX},${sourceY}L${targetX},${targetY}`;
      });

      nodeElements.attr('transform', d => {
        const xPos = d.fx != null ? d.fx : d.x;
        const yPos = d.fy != null ? d.fy : d.y;

        const x = typeof xPos === 'number' ? Math.max(15, Math.min(liveContainerWidth - 15, xPos)) : 0;
        const y = typeof yPos === 'number' ? Math.max(15, Math.min(liveContainerHeight - 15, yPos)) : 0;
        return `translate(${x},${y})`;
      });
    }

    // TEMPORARILY COMMENT OUT THE SIMULATION KICK TO TEST ZOOM ISSUE
    // simulation.alpha(0.3).restart(); // Previous state
    simulation.alpha(0.1).restart(); // Re-enable with a gentle kick
    simulation.alphaTarget(0); // Ensure this runs to keep the simulation cool
    console.log('[Graph Effect - Main D3] Gentle simulation kick (alpha 0.1) re-enabled. Alpha target set to 0.');

    // Cleanup for the main useEffect
    return () => {
      svg.on('click.diagnostic', null);
      // Do not stop simulation here as it's persistent. Stop on unmount.
      if (linkingState.current.tempLink) {
        linkingState.current.tempLink.remove();
      }
      linkingState.current = { sourceNode: null, tempLink: null, isLinking: false };
      if (clickTimeout.current) {
        clearTimeout(clickTimeout.current);
      }
    };
  }, [nodes, edges, onNodeClick, onNodeDoubleClick, onCreateEdge, onDeleteEdge, interactionMode, setInteractionMode, onBackgroundClick, isAnyPanelVisible, handleMouseMoveLinking, handleMouseUpLinking]);

  // Add a separate useEffect for stopping the simulation on component unmount
  useEffect(() => {
    return () => {
      if (simulationRef.current) {
        console.log('[Graph Unmount] Stopping simulation.');
        simulationRef.current.stop();
      }
    };
  }, []); // Empty dependency array means this runs once on mount for the return (cleanup) function

  // Handle keyboard delete for selected edges
  useEffect(() => {
    const handleKeyDown = (event) => {
      if ((event.key === 'Delete' || event.key === 'Backspace') && selectedEdge) {
        if (selectedEdge && selectedEdge._id && onDeleteEdge) {
          onDeleteEdge(selectedEdge._id);
          setSelectedEdge(null);
        }
      }
    };


    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [selectedEdge, onDeleteEdge]);

  useEffect(() => {
    if (svgRef.current) {
      setIsSvgMounted(true);
    }
    return () => setIsSvgMounted(false);
  }, []);




function dragBehavior(sim, svgInstance, containerWidth, containerHeight) {
  // Only return drag behavior if we're in select mode
  if (interactionMode !== 'select') {
    return d3.drag(); // Return empty drag behavior
  }
  
  return d3.drag()
    .on('start', function(event, d) {
      event.sourceEvent.stopPropagation();
      if (!event.active) sim.alphaTarget(0.3).restart();
      d.fx = Math.max(15, Math.min(containerWidth - 15, d.x));
      d.fy = Math.max(15, Math.min(containerHeight - 15, d.y));
    })
    .on('drag', function(event, d) {
      event.sourceEvent.stopPropagation();
      d.fx = Math.max(15, Math.min(containerWidth - 15, event.x));
      d.fy = Math.max(15, Math.min(containerHeight - 15, event.y));
      d.position = { x: d.fx, y: d.fy };
    })
    .on('end', async function(event, d) {
      const wasClick = Math.abs(event.dx) < 2 && Math.abs(event.dy) < 2;
      if (!wasClick && api?.updateNode) {
        await api.updateNode(d._id, { position: { x: d.fx, y: d.fy } })
          .catch(err => console.error("Failed to update node position:", err));
      }
    });
}








  const handleNodeClick = (event, d) => {
  console.log(`[Node Click Handler] Node: ${d._id}, Mode: ${interactionMode}`);
  console.log(`[Node Click Handler] Current linking state:`, linkingState.current);
  console.log(`[Node Click Handler] onCreateEdge function:`, onCreateEdge);
  
  if (event.defaultPrevented) {
    console.log('[Node Click Handler] DefaultPrevented, returning early');
    return;
  }

  if (interactionMode === 'connect') {
    console.log('[Node Click Handler] In connect mode');
    
    if (!linkingState.current.isLinking) {
      // Start a new connection
      console.log('🟢 STARTING connection from node:', d._id);
      linkingState.current = {
        isLinking: true,
        sourceNode: d,
        tempLink: d3.select(svgRef.current).append('path')
          .attr('class', 'temp-link')
          .attr('d', `M${d.x},${d.y}L${d.x},${d.y}`)
          .attr('stroke-dasharray', '5,5')
          .attr('stroke', '#B87333')
          .attr('stroke-width', 2)
          .attr('fill', 'none')
          .attr('marker-end', 'url(#arrow)')
          .raise()
      };
      
      console.log('🟢 Linking state after start:', linkingState.current);
      
      // Add visual feedback to the source node
      d3.select(event.currentTarget).select('circle')
        .attr('stroke', '#B87333')
        .attr('stroke-width', 3);
        
    } else {
      // Complete the connection
      console.log('🔵 COMPLETING connection');
      console.log('🔵 Source node:', linkingState.current.sourceNode?._id);
      console.log('🔵 Target node:', d._id);
      
      if (linkingState.current.sourceNode && linkingState.current.sourceNode._id !== d._id) {
        console.log('🟢 Creating edge from', linkingState.current.sourceNode._id, 'to', d._id);
        console.log('🟢 onCreateEdge function exists:', !!onCreateEdge);
        
        if (onCreateEdge) {
          try {
            console.log('🟢 Calling onCreateEdge...');
            const result = onCreateEdge(linkingState.current.sourceNode._id, d._id);
            console.log('🟢 onCreateEdge result:', result);
          } catch (error) {
            console.error('🔴 Error calling onCreateEdge:', error);
          }
        } else {
          console.error('🔴 onCreateEdge is not defined!');
        }
      } else if (linkingState.current.sourceNode?._id === d._id) {
        console.log('🟡 Cannot connect node to itself');
      } else {
        console.log('🔴 No source node found');
      }
      
      // Clean up
      console.log('🔵 Cleaning up linking state');
      linkingState.current.tempLink?.remove();
      
      // Reset visual feedback
      d3.selectAll('.node circle')
        .attr('stroke', '#1B5E20')
        .attr('stroke-width', 1.5);
      
      linkingState.current.isLinking = false;
      linkingState.current.sourceNode = null;
      linkingState.current.tempLink = null;
    }
    return;
  }

  // Rest of your existing select mode logic...
};





  // Handle edge deletion
  const handleDeleteEdge = () => {
    if (selectedEdge && selectedEdge._id && onDeleteEdge) {
      onDeleteEdge(selectedEdge._id);
      setSelectedEdge(null);
    }
  };

  return (
    <div className="graph-wrapper" onClick={(e) => {
      console.log('[Graph Wrapper Click] Target:', e.target, 'CurrentTarget:', e.currentTarget);
      if (e.target === e.currentTarget) {
        console.log('[Graph Wrapper Click] Click was directly on wrapper.');
        if (onBackgroundClick) {
          onBackgroundClick();
        }
      }
    }}>
      {nodes && nodes.length > 0 ? (
        <svg ref={svgRef} className="graph-svg" />
      ) : (
        <div className="graph-svg empty-graph-placeholder">
          <div className="empty-graph-message">
            <p>This canvas is empty. Start by creating a new node!</p>
          </div>
        </div>
      )}
      <div className="graph-controls">
        {selectedEdge && (
          <button
            onClick={handleDeleteEdge}
            className="button danger"
          >
            <TrashIcon style={{ width: '1rem', height: '1rem', marginRight: '0.5rem' }} />
            Remove Edge
          </button>
        )}
        <div className="button-row">
          <button
            onClick={() => {
              // Pass current dimensions when calling onCreateNew
              if (onCreateNew) {
                onCreateNew({
                  graphWidth: graphDimensionsRef.current.width,
                  graphHeight: graphDimensionsRef.current.height
                });
              }
            }}
            className="button primary"
          >
            <PlusIcon style={{ width: '1rem', height: '1rem', marginRight: '0.5rem' }} />
            Create New Node
          </button>





 <button
  onClick={() => {
    const newMode = interactionMode === 'connect' ? 'select' : 'connect';
    setInteractionMode(newMode);
    
    // Reset any existing connection state
    if (newMode === 'select') {
      d3.select(svgRef.current).selectAll('.temp-link').remove();
      linkingState.current = { sourceNode: null, tempLink: null, isLinking: false };
    }
  }}
  className={`button accent ${interactionMode === 'connect' ? 'active' : ''}`}
>
  <LinkIcon style={{ width: '1rem', height: '1rem', marginRight: '0.5rem' }} />
  {interactionMode === 'connect' ? 'Stop Connecting' : 'Connect Nodes'}
</button>



        </div>
      </div>
    </div>
  );
