import React, { useRef, useEffect, useState } from 'react';
import * as d3 from 'd3';
import './Graph.css';
import api from '../services/api.js';
import PlusIcon from './icons/PlusIcon';
import LinkIcon from './icons/LinkIcon';
import TrashIcon from './icons/TrashIcon';

const Graph = ({ nodes = [], edges = [], onNodeClick, onNodeDoubleClick, onCreateNew, onCreateEdge, onDeleteEdge, interactionMode = 'select', setInteractionMode, onBackgroundClick }) => {
  const svgRef = useRef();
  const linkingState = useRef({ sourceNode: null, tempLink: null, isLinking: false });
  const [selectedEdge, setSelectedEdge] = useState(null);
  const clickTimeout = useRef(null);

  console.log('Graph received:', {
    nodeCount: nodes.length,
    edgeCount: edges.length,
    mode: interactionMode
  });

  // Debug: Log the actual nodes and edges data
  console.log('Nodes data:', JSON.stringify(nodes, null, 2));
  console.log('Edges data:', JSON.stringify(edges, null, 2));

  useEffect(() => {
    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();

    if (!nodes || nodes.length === 0) {
      return;
    }

    const containerWidth = svgRef.current.clientWidth;
    const containerHeight = svgRef.current.clientHeight;

    svg
      .attr('viewBox', [0, 0, containerWidth, containerHeight])
      .attr('preserveAspectRatio', 'xMidYMid meet');

    // Add a group that will contain all zoomable/pannable elements
    const g = svg.append('g').attr('class', 'zoom-group');

    // Add background rect to catch clicks and zoom events
    // This rect is appended to the SVG directly so it's behind the zoom group,
    // but zoom events will be handled by the SVG itself.
    svg.append('rect')
      .attr('class', 'background')
      .attr('width', '100%')
      .attr('height', '100%')
      .attr('fill', 'transparent')
      .on('click', (event) => {
        if (event.target.classList.contains('background')) {
          setSelectedEdge(null);
          if (onBackgroundClick) {
            onBackgroundClick();
          }
        }
      });

    // Add arrow marker definition for edges
    svg.append("defs").selectAll("marker")
      .data(["arrow"])
      .enter().append("marker")
      .attr("id", d => d)
      .attr("viewBox", "0 -5 10 10")
      .attr("refX", 20) // Increased from 15 to position arrow better
      .attr("refY", 0)
      .attr("markerWidth", 6)
      .attr("markerHeight", 6)
      .attr("orient", "auto")
      .append("path")
      .attr("d", "M0,-5L10,0L0,5")
      .attr("fill", "#6c757d");

    // Initialize node positions if not set
    nodes.forEach(node => {
      if (!node.position || (node.position.x === null && node.position.y === null)) {
        node.x = Math.random() * (containerWidth - 100) + 50;
        node.y = Math.random() * (containerHeight - 100) + 50;
        node.position = { x: node.x, y: node.y };
      } else {
        node.x = node.position.x;
        node.y = node.position.y;
      }
      // Don't fix positions by default to allow force layout to work
      node.fx = null;
      node.fy = null;
    });

    // Create a map of nodes by ID for easy lookup
    const nodeMap = {};
    nodes.forEach(node => {
      nodeMap[node._id] = node;
    });

    // Process edges to ensure they reference actual node objects
    const processedEdges = edges.map(edge => ({
      id: edge._id,
      source: nodeMap[edge.source] || edge.source,
      target: nodeMap[edge.target] || edge.target,
      type: edge.type,
      _id: edge._id
    }));

    const simulation = d3.forceSimulation(nodes)
      .force('link', d3.forceLink(processedEdges).id(d => d._id).distance(150))
      .alpha(0)
      .alphaDecay(1)
      .alphaTarget(0);

    // Fix node positions immediately
    nodes.forEach(node => {
      if (node.position) {
        node.fx = node.position.x;
        node.fy = node.position.y;
      } else {
        node.fx = Math.random() * (containerWidth - 100) + 50;
        node.fy = Math.random() * (containerHeight - 100) + 50;
        node.position = { x: node.fx, y: node.fy };
      }
    });

    // Create edge paths with arrows, appended to the zoomable group 'g'
    const linkElements = g.append('g')
      .attr('class', 'links')
      .selectAll('path')
      .data(processedEdges)
      .enter().append('path')
      .attr('class', 'link')
      .attr('stroke', '#6c757d')
      .attr('stroke-width', 2)
      .attr('fill', 'none')
      .attr('marker-end', 'url(#arrow)')
      .on('click', handleEdgeClick);

    const nodeElements = g.append('g')
      .attr('class', 'nodes')
      .selectAll('g')
      .data(nodes)
      .enter().append('g')
      .attr('class', 'node')
      .call(dragBehavior(simulation));

    nodeElements.append('circle')
      .attr('r', 12)
      .on('click', handleNodeClick);

    nodeElements.append('text')
      .text(d => d.title || d._id?.substring(0, 6))
      .attr('x', 16)
      .attr('y', 5)
      .attr('text-anchor', 'start')
      .on('click', handleNodeClick);

    function handleEdgeClick(event, d) {
      event.stopPropagation();
      if (interactionMode === 'select') {
        d3.selectAll('.link').classed('selected', false);
        d3.select(event.currentTarget).classed('selected', true);
        setSelectedEdge(d);
        if (onNodeClick) onNodeClick(null);
      }
    }

    svg.on('click', (event) => {
      if (event.target.classList.contains('background') || event.target === svgRef.current) {
        d3.selectAll('.link').classed('selected', false);
        setSelectedEdge(null);
        if (onBackgroundClick) {
          onBackgroundClick();
        }
      }
    });

    function ticked() {
      linkElements.attr('d', d => {
        // Positions are now relative to the zoom group, no need to clamp here
        // Clamping should ideally happen based on node positions within the simulation boundaries
        // or be handled by the viewport itself if strict boundaries are desired.
        const sourceX = d.source.x;
        const sourceY = d.source.y;
        const targetX = d.target.x;
        const targetY = d.target.y;
        
        return `M${sourceX},${sourceY}L${targetX},${targetY}`;
      });

      nodeElements.attr('transform', d => {
        // Positions are now relative to the zoom group
        const x = d.x;
        const y = d.y;
        return `translate(${x},${y})`;
      });
    }

    simulation.on('tick', ticked);
    
    // Initialize zoom
    const zoomBehavior = d3.zoom()
      .scaleExtent([0.1, 4]) // Example scale extent: 0.1x to 4x
      .filter(event => {
        // Allow wheel events for zooming everywhere
        if (event.type === 'wheel') {
          return true;
        }
        // For mousedown events, check if the target is part of a node or link
        if (event.type === 'mousedown') {
          let targetElement = event.target;
          while (targetElement && targetElement !== svg.node()) {
            const selection = d3.select(targetElement);
            if (selection.classed('node') || selection.classed('link')) {
              // If mousedown is on a node or link, zoom should not activate here.
              // Let the node/link's own click/drag handlers manage the event.
              return false; 
            }
            targetElement = targetElement.parentNode;
          }
          // If mousedown is on the background, allow zoom to proceed for panning (e.g., left-click drag)
          return !event.button; 
        }
        // Fallback for other event types (though mousedown and wheel are primary for zoom)
        return (!event.ctrlKey || event.type === 'wheel') && !event.button;
      })
      .on('zoom', (event) => {
        g.attr('transform', event.transform);
      });

    svg.call(zoomBehavior);
    
    // Initial transform if needed (e.g., center graph, though force layout handles initial positions)
    // If you want to set an initial zoom/pan:
    // const initialTransform = d3.zoomIdentity.translate(containerWidth / 2, containerHeight / 2).scale(0.8);
    // svg.call(zoomBehavior.transform, initialTransform);
    // For now, let's start with default 1:1 scale at 0,0 origin of the zoom group

    return () => {
      simulation.stop();
      if (linkingState.current.tempLink) {
        linkingState.current.tempLink.remove();
      }
      linkingState.current = { sourceNode: null, tempLink: null, isLinking: false };
      if (clickTimeout.current) {
        clearTimeout(clickTimeout.current);
      }
    };
  }, [nodes, edges, onNodeClick, onNodeDoubleClick, onCreateEdge, interactionMode, onBackgroundClick]);

  // Handle keyboard delete for selected edges
  useEffect(() => {
    const handleKeyDown = (event) => {
      if ((event.key === 'Delete' || event.key === 'Backspace') && selectedEdge) {
        if (selectedEdge && selectedEdge._id && onDeleteEdge) {
          onDeleteEdge(selectedEdge._id);
          setSelectedEdge(null); // Deselect after deletion
        }
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [selectedEdge, onDeleteEdge]);

  function dragBehavior(simulation) {
    function dragstarted(event, d) {
      if (interactionMode === 'connect') {
        linkingState.current.isLinking = true;
        linkingState.current.sourceNode = d;
        linkingState.current.tempLink = d3.select(svgRef.current).append('path')
          .attr('class', 'temp-link link')
          .attr('d', `M${d.x},${d.y}L${d.x},${d.y}`)
          .attr('stroke-dasharray', '5,5')
          .attr('stroke', '#6c757d')
          .attr('stroke-width', 2)
          .attr('fill', 'none')
          .attr('marker-end', 'url(#arrow)');
      } else {
        if (!event.active) simulation.alphaTarget(0.3).restart();
        d.fx = d.x;
        d.fy = d.y;
      }
    }

    function dragged(event, d) {
      if (interactionMode === 'connect' && linkingState.current.isLinking) {
        linkingState.current.tempLink
          .attr('d', `M${linkingState.current.sourceNode.x},${linkingState.current.sourceNode.y}L${event.x},${event.y}`);
      } else {
        // Adjust dragged coordinates by current zoom transform if interacting with SVG coordinates directly
        // However, d3.drag usually provides coordinates in the local space of the element being dragged.
        // For nodes within a transformed 'g', their d.x/d.y are in the 'g's coordinate system.
        // d3-drag event.x and event.y are also in the coordinate system of the *parent* of the dragged element.
        // So if nodes are children of 'g', event.x/y should be correct.
        
        // If event.x/y were screen coordinates, you'd need to invert the zoom:
        // const currentTransform = d3.zoomTransform(svgRef.current);
        // const [newX, newY] = currentTransform.invert([event.x, event.y]);
        // d.fx = newX;
        // d.fy = newY;
        
        d.fx = event.x; // Assuming event.x/y are in the coordinate system of the zoomable 'g'
        d.fy = event.y;
        d.position = { x: event.x, y: event.y };
      }
    }

    async function dragended(event, d) {
      if (interactionMode === 'connect' && linkingState.current.isLinking) {
        if (linkingState.current.tempLink) {
          linkingState.current.tempLink.remove();
          linkingState.current.tempLink = null;
        }

        const targetElement = event.sourceEvent.target;
        // console.log('dragended targetElement:', targetElement); // Removed for debugging

        // More robust check: Ensure targetElement is a DOM Element or SVGElement
        if (targetElement && (targetElement instanceof Element || targetElement instanceof SVGElement)) {
          let targetNodeElement = targetElement;

          // Loop to find the actual '.node' G element
          // Ensure targetNodeElement remains a valid element during traversal
          while (targetNodeElement &&
                 (targetNodeElement instanceof Element || targetNodeElement instanceof SVGElement) && // Check instance type in loop
                 !d3.select(targetNodeElement).classed('node')) { // This is where .classed is called
            // console.log('dragended in while loop, targetNodeElement before parent:', targetNodeElement); // Removed for debugging
            if (!targetNodeElement.parentNode || !(targetNodeElement.parentNode instanceof Element || targetNodeElement.parentNode instanceof SVGElement) ) {
                targetNodeElement = null; // Stop if no valid parentNode
                break;
            }
            targetNodeElement = targetNodeElement.parentNode;
          }
          // console.log('dragended after while loop, targetNodeElement:', targetNodeElement); // Removed for debugging

          // Check if a valid node (which should be a G element with class 'node') was found
          if (targetNodeElement &&
              (targetNodeElement instanceof Element || targetNodeElement instanceof SVGElement) &&
              d3.select(targetNodeElement).classed('node')) { // This is the other place .classed is called
            // console.log('dragended attempting to create edge, targetNodeElement:', targetNodeElement); // Removed for debugging
            const targetDatum = d3.select(targetNodeElement).datum();

            if (targetDatum && targetDatum._id && linkingState.current.sourceNode && targetDatum._id !== linkingState.current.sourceNode._id) {
              if (onCreateEdge) {
                onCreateEdge(linkingState.current.sourceNode._id, targetDatum._id);
              }
            }
          }
        }
        // If targetElement wasn't a valid element, or if no valid '.node' was found,
        // this point is reached, and we just reset linking state.
        linkingState.current = { sourceNode: null, tempLink: null, isLinking: false };
      } else {
        // This part handles dragging nodes for repositioning, not connecting
        if (!event.active) simulation.alphaTarget(0);
        d.fx = event.x;
        d.fy = event.y;
        d.position = { x: event.x, y: event.y };

        try {
          if (d._id) {
            await api.updateNodePosition(d._id, d.position);
          }
        } catch (err) {
          console.error('Failed to save node position:', err);
        }
      }
    }

    return d3.drag()
      .on('start', dragstarted)
      .on('drag', dragged)
      .on('end', dragended);
  }

  const handleNodeClick = (event, d) => {
    event.stopPropagation();
    setSelectedEdge(null);

    if (interactionMode === 'connect') {
      // Handle connection mode logic
      return;
    }

    if (clickTimeout.current) {
      // Double click detected
      clearTimeout(clickTimeout.current);
      clickTimeout.current = null;
      if (onNodeDoubleClick) {
        onNodeDoubleClick(d);
      }
    } else {
      // First click - wait for potential second click
      clickTimeout.current = setTimeout(() => {
        clickTimeout.current = null;
        if (onNodeClick) {
          onNodeClick(d);
        }
      }, 250); // 250ms delay to detect double click
    }
  };

  // Handle edge deletion
  const handleDeleteEdge = () => {
    if (selectedEdge && selectedEdge._id && onDeleteEdge) {
      onDeleteEdge(selectedEdge._id);
      setSelectedEdge(null);
    }
  };

  return (
    <div className="graph-wrapper" onClick={(e) => {
      if (e.target === e.currentTarget) {
        if (onBackgroundClick) {
          onBackgroundClick();
        }
      }
    }}>
      {nodes && nodes.length > 0 ? (
        <svg ref={svgRef} className="graph-svg" />
      ) : (
        <div className="graph-svg empty-graph-placeholder">
          <div className="empty-graph-message">
            <p>This canvas is empty. Start by creating a new node!</p>
            {/* The main "Create New Node" button is in graph-controls below */}
          </div>
        </div>
      )}
      <div className="graph-controls">
        {selectedEdge && (
          <button
            onClick={handleDeleteEdge}
            className="button danger"
          >
            <TrashIcon style={{ width: '1rem', height: '1rem', marginRight: '0.5rem' }} />
            Remove Edge
          </button>
        )}
        <div className="button-row">
          <button onClick={onCreateNew} className="button primary">
            <PlusIcon style={{ width: '1rem', height: '1rem', marginRight: '0.5rem' }} />
            Create New Node
          </button>
          <button
            onClick={() => setInteractionMode(prevMode => prevMode === 'connect' ? 'select' : 'connect')}
            className={`button secondary ${interactionMode === 'connect' ? 'active' : ''}`}
          >
            <LinkIcon style={{ width: '1rem', height: '1rem', marginRight: '0.5rem' }} />
            {interactionMode === 'connect' ? 'Stop Connecting' : 'Connect Nodes'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Graph;
