import React, { useState, useEffect } from 'react';
import api from '../services/api';
import './FileList.css';

const FileList = ({ onFileSelect }) => {
  const [files, setFiles] = useState([]);
  const [newFileName, setNewFileName] = useState('');
  const [isCreating, setIsCreating] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    loadFiles();
  }, []);

  const loadFiles = async () => {
    try {
      const response = await api.getFiles();
      setFiles(response.data);
    } catch (err) {
      console.error('Error loading files:', err);
      setError('Failed to load files. Please try again.');
    }
  };

  const handleCreateFile = async (e) => {
    e.preventDefault();
    const trimmedName = newFileName.trim();
    
    if (!trimmedName) {
      setError('Please enter a file name');
      return;
    }

    // Check for duplicate names
    if (files.some(file => file.name.toLowerCase() === trimmedName.toLowerCase())) {
      setError('A file with this name already exists');
      return;
    }

    setError('');
    setIsCreating(true);

    try {
      const response = await api.createFile(trimmedName);
      setNewFileName('');
      await loadFiles();
      // Optionally auto-select the newly created file
      onFileSelect(response.data);
    } catch (err) {
      console.error('Error creating file:', err);
      setError('Failed to create file. Please try again.');
    } finally {
      setIsCreating(false);
    }
  };

  const handleDoubleClick = (file) => {
    onFileSelect(file);
  };

  return (
    <div className="file-list-container">
      <h2>Files</h2>
      <form onSubmit={handleCreateFile} className="file-create-form">
        <input
          type="text"
          value={newFileName}
          onChange={(e) => {
            setNewFileName(e.target.value);
            setError(''); // Clear error when user types
          }}
          placeholder="Enter file name"
          className="file-input"
          disabled={isCreating}
        />
        <button 
          type="submit" 
          className="create-file-btn"
          disabled={isCreating}
        >
          {isCreating ? 'Creating...' : 'Create File'}
        </button>
      </form>
      {error && <div className="error-message">{error}</div>}
      <div className="file-list">
        {files.map(file => (
          <div
            key={file._id}
            className="file-item"
            onDoubleClick={() => handleDoubleClick(file)}
          >
            <span className="file-number">#{file.srNo}</span>
            <span className="file-name">{file.name}</span>
            <span className="file-nodes-count">{file.nodes?.length || 0} nodes</span>
          </div>
        ))}
        {files.length === 0 && (
          <div className="empty-files">
            No files yet. Create your first file!
          </div>
        )}
      </div>
    </div>
  );
};

export default FileList;
